mapper.select:
        SELECT r.*
        FROM region r
        WHERE r.region_division_NUM = #{division_NUM} AND (
            #{theme_NUM} = 1 OR r.region_NUM IN (
                SELECT tr.themeregion_region_NUM
                FROM themeregion tr
                WHERE tr.themeregion_theme_NUM = #{theme_NUM}
            )
        )

SELECT p.*
FROM place p
WHERE p.place_region_NUM = #{cri.region_NUM} AND (
    #{cri.theme_NUM} = 1 OR p.place_NUM IN (
        SELECT tp.themeplace_place_NUM
        FROM themeplace tp
        WHERE tp.themeplace_theme_NUM = #{cri.theme_NUM}
    )
)

        SELECT p.*
        FROM place p
        JOIN placetype pt ON p.place_NUM = pt.placetype_place_NUM
        JOIN placetypelist ptl ON pt.placetype_placetypelist_NUM = ptl.placetypelist_NUM
        WHERE p.place_region_NUM = #{cri.region_NUM}
          AND (#{cri.theme_NUM} = 1 OR p.place_NUM IN (
                SELECT tp.themeplace_place_NUM
                FROM themeplace tp
                WHERE tp.themeplace_theme_NUM = #{cri.theme_NUM} 
              )
          )
          AND #{cri.placetypelist_NUM} = 1 OR (ptl.placetypelist_NUM = #{cri.placetypelist_NUM})
        ORDER BY p.place_like DESC;

SELECT p.*
FROM place p
WHERE p.place_like > 0 AND p.place_region_NUM = #{cri.region_NUM} AND (
    #{cri.theme_NUM} = 1 OR p.place_NUM IN (
        SELECT tp.themeplace_place_NUM
        FROM themeplace tp
        WHERE tp.themeplace_theme_NUM = #{cri.theme_NUM}
    )
)
ORDER BY p.place_like DESC
